import './Tiptap.css'
import { EditorContent, useEditor } from "@tiptap/react";
import StarterKit from "@tiptap/starter-kit";

const Tiptap = () => {
  const editor = useEditor({
    extensions: [StarterKit],
    content: "<p>Edit me üëçüëç</p>",
  });

  if (!editor) {
    return null
  }


  return (
    <div>
      <div>
        <button
          onClick={() => editor.chain().focus().toggleBold().run()}
          disabled={
            !editor.can()
              .chain()
              .focus()
              .toggleBold()
              .run()
          }
          className={`bg-gray-400 px-4 my-2 rounded-md ${editor.isActive('bold') ? 'is-active' : 'non-active'}`}>
          <strong>B</strong>
        </button>
        <EditorContent editor={editor} className='py-2 px-2' />
        <button className='bg-gray-200 px-4 font-semibold rounded-md hover:*:text-xl  '>Save</button>
      </div>
    </div>
  )
};

export default Tiptap;
